const GET_LOGS = "GET_LOGS"
const DELETE_LOGS = "DELETE_LOGS"
const defaultState = {
    logs: []
}
export default function logsReducer(state = defaultState, action)
{
    switch (action.type)
    {
        case GET_LOGS:
            return {...state, logs: action.payload}
        case DELETE_LOGS:
            return {...state, logs: []}
        default: 
            return state;
    }
}
export const getLogs = logs => ({type: GET_LOGS, payload: logs})
export const deleteLogs = () => ({type: DELETE_LOGS})
