import React, {useEffect, useState} from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Button, Col, Row} from 'react-bootstrap';

import './watchman.css'
import { setPopupDisplay, setSelectedEmployee } from '../../../reducers/employeeReducer';
import { getCabs } from '../../../actions/employees';
const EmployeeItem = ({employee, key}) => {
    const dispatch = useDispatch()
    const currentUser = useSelector(state => state.user.currentUser)
    function giveKeyHandle(e)
    {
        dispatch(setSelectedEmployee(employee))
        dispatch(getCabs(employee))
        dispatch(setPopupDisplay('flex'))
    }
    function takeKeyHandle(e)
    {
        dispatch(setSelectedEmployee(employee))
        dispatch(getCabs(employee))
        dispatch(setPopupDisplay('flex'))
    }
    return (
        <tr key={key}>
            <td>{employee.emplid}</td>
            <td>{employee.first_name}</td>
            <td>{employee.last_name}</td>
            <td colSpan="2"><Button onClick = {(e) => giveKeyHandle(e)}>Выдать ключ</Button></td>
            <td><Button variant="warning" onClick = {(e) => takeKeyHandle(e)}>Принять ключ</Button></td>
        </tr>
    );
};
export default EmployeeItem;
