import React, { useState } from 'react';
import './input.css'

const Input = (props) => {
     function HandleClear () {
        props.setValue('')
    }
    return (
        <input onChange={(event)=> props.setValue(event.target.value)}
               value={props.value}
               type={props.type}
               placeholder={props.placeholder}/>
    );
};

export default Input;
