import React from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { deleteAvatar, uploadAvatar } from '../../actions/user';
import avatarLogo from '../../assets/img/defaultAvatar.svg'
import { API_URL } from '../../config';
import './profile.css'
const Profile = () =>
{
    const dispatch = useDispatch()
    const currentUser = useSelector(state => state.user.currentUser)
    const avatar = currentUser.avatar ? (API_URL + currentUser.avatar) : avatarLogo
    function changeHandler(e) {
        const file = e.target.files[0]
        dispatch(uploadAvatar(file))
        e.target.value = ''
    }
    return (
        <div>
            <img className="my__avatar" src={avatar} alt=''></img>
            <button className="delete__avatar" onClick={() => dispatch(deleteAvatar())}>Удалить аватар</button>
            <label htmlFor ="disk__upload-input" className="disk__upload-label">Загрузить аватар</label>
            <input accept="image/*" type="file" onChange={(e) => changeHandler(e)} id='disk__upload-input' className="disk__upload-input"></input>
        </div>
    )
}
export default Profile;
