import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Input from '../../utils/input/Input';


const Popup = () =>{
    const [dirName, setDirName] = useState('')
    const popupDisplay = useSelector(state => state.files.popupDisplay)
    const dispatch = useDispatch()
    return(
        <div className = 'popup' onClick ={()=> dispatch()} style = {{display: popupDisplay}}>
            <div className="popup__content">
                <div className="popup__header">
                    <div className="popup__title">Создать новую папку</div>
                    <button className="popup__close">X</button>
                </div>
                <Input type ="text" placeholder = "Введите название папки" value={dirName} setValue={setDirName}/>
                <button className="popup__create">Создать папку</button>
            </div>
        </div>
    );
};

export default Popup;
