import React from 'react';
import {useDispatch, useSelector} from 'react-redux';
import {setPopupDisplay} from '../../reducers/fileReducer';
import Input from '../../utils/input/Input';
import {createDir} from "../../actions/file"
import {useState} from 'react'
import {useRef} from 'react'
const Popup = () =>{
    const [dirName, setDirName] = useState('')
    const popupDisplay = useSelector(state => state.files.popupDisplay)
    const dispatch = useDispatch()
    const currentDir = useSelector(state => state.files.currentDir)

    function createHandler(childRef) 
    {
        dispatch(createDir(currentDir, dirName))
        dispatch(setPopupDisplay('none'))
        childRef.current.HandleClear();
    }



    const childRef = useRef();
    return(
        <div className = 'popup' onClick ={()=> dispatch(setPopupDisplay('none'))} style = {{display: popupDisplay}}>
            <div className="popup__content" onClick={(event => event.stopPropagation())}>
                <div className="popup__header">
                    <div className="popup__title">Создать новую папку</div>
                    <button className="popup__close" onClick ={()=> dispatch(setPopupDisplay('none'))}>X</button>
                </div>
                <Input ref = {childRef} type ="text" placeholder = "Введите название папки" value={dirName} setValue={setDirName}/>
                <button className="popup__create" onClick={() => createHandler(childRef)}>Создать папку</button>
            </div>
        </div>
    );
};

export default Popup;
